def reverseList(head):    pre = None    while head:        cur = head             # 储存当前head节点的位置        head = head.next     # 改变head的位置        cur.next = pre     # 反转指针        pre = cur           # 挪动pre    return predef reverseList2(head):     # 递归的做法，但还没有搞懂    if not head or not head.next:        return head    new_head = reverseList(head.next)       # 他是怎么循环到最后一个节点，然后在循环往前的？    new_node = head.next    new_node.next = head    head.next = None    return new_head