class Node(object):    def __init__(self, data):        self.data = data        self.next = None    def swapPairs(self, head):        dummy = pre = Node(0)        dummy.next = head              # 新节点的next 为 None，先设置一个指针        while pre.next.next and pre.next:            node1 = pre.next            node2 = node1.next            _next = node2.next            pre.next = node2            node2.next = node1            node1.next = _next